PARAMETER.DEFAULT:
do {
    use DBI;

    my $dbh = DBI->connect(
        sprintf(
            'DBI:mysql:database=%s;host=%s;port=%s',
            $_context{db_name},
            $_context{db_host},
            $_context{db_port},
        ),
        $_context{db_user},
        $_context{db_pass}
    )
        or die "database connection has failed";

    $dbh->do('set names "UTF8"');

    $dbh;
}
